jQuery(function(){$("#fileupload").fileupload({add:function(e,s){types=/(\.|\/)(gif|jpe?g|png)$/i,file=s.files[0],types.test(file.type)&&types.test(file.name)?$(".progress").fadeIn(100):alert(file.name+"is not a gif, jpeg, or png image file")},progress:function(e,s){$(".progress").fadeIn(100);var t=parseInt(100*(s.loaded/s.total),10);$(".progress .bar").css("width",t+"%")},done:function(){$(".progress").fadeOut(100)}})});