jQuery(function(){function e(){$(".flash_message").html("<div class='message_#{type}'><div class='alert_message'>#{msg}</div></div>"),showFlashMessages(),hideFlashMessages()}$("#fileupload").fileupload({add:function(e,s){types=/(\.|\/)(gif|jpe?g|png)$/i,file=s.files[0],types.test(file.type)&&types.test(file.name)?($(".progress").fadeIn(100),s.submit()):alert(file.name+"is not a gif, jpeg, or png image file")},progress:function(e,s){$(".progress").fadeIn(100);var a=parseInt(100*(s.loaded/s.total),10);$(".progress .bar").css("width",a+"%")},done:function(){$(".progress").fadeOut(100),e("success","Успешно загружена")}})});